		const initialState = hooks.initialState || {};
		delete hooks.history;
		delete hooks.initialState;

		const plugin = new Plugin();
		plugin.use(hooks);

		const app = {
			//properties
			_models:[],
			_router:null,
			_history:null,
			_plugin:plugin,
			_getProvider:null,
